    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}
{% block title %}Connexion{% endblock %}
{% block body %}

<div class=\"p1\">
    <div class=\"d-flex justify-content-center\">
        <form action=\"{{ path('app_login') }}\" method=\"post\">
                {% if error %}
                <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
                {% endif %}
                <div class=\"form-group\">
                    <h1> Connexion </h1>
                    <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\" class=\"form-control\"
                        placeholder=\"Email\" required autofocus>
                </div>

                <div class=\"form-group\">
                    <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\"
                        placeholder=\"Votre mot de passe\" required>
                </div>
                <input type=\"hidden\" name=\"_csrf_token\" value=\"{{ csrf_token('authenticate') }}\">
                <div class=\"form-group\">
                    <input type=\"checkbox\" id=\"remember_me\" name=\"_remember_me\" checked />
                    <label for=\"remember_me\">Restez connecté ?</label>
                </div>
                <div class=\"form-group\">
                    <button type=\"submit\" class=\"btn btn-warning\">Se connecter</button>

                    <p>Vous n'êtes pas inscrit ? <a class=\"linklog\"
                            href=\"{{ path('security_registration') }}\">Inscrivez-vous
                            gratuitement</a></p>
                </div>
        </form>
    </div>
</div>
{% endblock %}", "front/security/login.html.twig", "/var/www/html/PROJET_PEDA/projetannuaire/templates/front/security/login.html.twig");
